    private void savePalette(String paletteStr, String categoryStr) {
        log.debug("saving palette with category name {}", categoryStr);

        var paletteArray = paletteStr.split(" ");

        if (paletteArray.length % 4 != 0) {
            log.debug("palette with name {} entries are wrong, has {} inputs", categoryStr, paletteArray.length);
            return;
        }

        log.debug("finding category with name {}", categoryStr);
        var categoryOptional = categoryRepository.findByNameIgnoreCase(categoryStr);

        if (categoryOptional.isEmpty()) {
            log.debug("category with name {} is not present", categoryStr);
            return;
        }

        var category = categoryOptional.get();

        var step = 0;
        for (int i = 0; i < paletteArray.length; i = step * 4) {
            var palette = new Palette();
            palette.setColor1(paletteArray[i]);
            palette.setColor2(paletteArray[i + 1]);
            palette.setColor3(paletteArray[i + 2]);
            palette.setColor4(paletteArray[i + 3]);
            palette.setCreationDate(Instant.now());
            palette.setIsApproved(true);

            paletteRepository.save(palette);
            category.addPalette(palette);

            var saved = paletteRepository.save(palette);
            categoryRepository.save(category);
            step++;
        }
    }

    private void saveCategories() {
        log.debug("Saving categories");
        var categories = "Pastel Neon Gold Vintage Retro Light Dark Warm Cold Summer Fall Winter Spring Rainbow Night Space Earth Nature Sunset Skin Food Cream Coffee Christmas Halloween Wedding Kids Happy";

        var categoriesArray = categories.split(" ");
        for (String categoryStr : categoriesArray) {
            var categoryObj = new Category(categoryStr.toLowerCase());
            categoryObj.setIsApproved(true);
            categoryRepository.save(categoryObj);
        }
    }
